function loadHorizonatalMove(){let e=document.querySelectorAll("div.horizontalSlider > button"),t=document.querySelector("div.slider"),l=t.children[0].offsetWidth,n=t.children.length;for(let o=0;o<e.length;o++)e[o].addEventListener("click",(()=>{horizontalMove(o,t,l,n*l)}));timing(e[1]),makeBullets(n)}function timing(e){setInterval((()=>{e.click()}),5e3)}function horizontalMove(e,t,l,n){let o=parseFloat(t.style.transform.match(/-?[0-9]+/g));0===e?o+=l:o-=l,o>=l&&(o=-(n-l)),o<=-n&&(o=0),move(t,o)}function move(e,t){e.style.transform="translateX("+t+"px)"}function makeBullets(e){let t=document.querySelector("div.bullets"),l=document.createElement("div");l.classList.add("bullets");for(let t=0;t<e;t++){let e=document.createElement("span");0===t&&e.classList.add("active"),l.appendChild(e)}t=l}window.addEventListener("DOMContentLoaded",(()=>{loadHorizonatalMove()}));